mapper.select:
select c.activity_id as "activity.activityId", c.create_time as "createTime", c.content as "content", f.school_num as "from.schoolNum", f.name as "from.name", t.school_num as "to.schoolNum", t.name as "to.name" from t_comment c, t_user f, t_activity a where c.from=f.school_num and c.activity_id=a.activity_id
and c.from=#{from}
and l.activity_id=#{activityId}
left join on t_user t on c.to=t.school_num ORDER by c.create_time asc
limit ${start},${count}

mapper.delete:
delete from t_comment
where
and comment_id=#{commentId}
